INSERT INTO `fruit`.`product` (`name`, `price`, `description`, `discount`, `quantity`, `status`,`category_id`) VALUES ('Dừa xiêm trọc Bến Tre size M (450-600g/trái)', '20700', '<div><h3><strong><span style=\"font-size:14px\">Ưu điểm của dừa Xiêm&nbsp;trọc Bến Tre là gì?</span></strong></h3><p><span style=\"font-size:14px\">- Nhỏ gọn, không chiếm quá nhiều diện tích vì đã gọt sạch vỏ hoàn toàn.</span></p><p><span style=\"font-size:14px\">- Có thể giữ được rất lâu ở điều kiện bình thường và thời gian sẽ kéo dài đến 45 ngày nếu được bảo quản ở nhiệt độ từ 2 - 5 độ C.</span></p><p><span style=\"font-size:14px\">- Thích hợp để bạn mang theo khi đi du lịch, dã ngoại, mang đi học hoặc đi làm.</span></p><p><span style=\"font-size:14px\">- Được chọn từ dừa xiêm Bến Tre nên đảm bảo có hương vị thơm ngon, mát lành từ tự nhiên, giúp giải khát tức thì cho ngày hè oi bức.</span></p><h3><strong><span style=\"font-size:14px\">Hướng dẫn uống dừa Xiêm&nbsp;trọc Bến Tre</span></strong></h3><p><span style=\"font-size:14px\">Với dừa gọt trọc Bến Tre, bạn sẽ có thể dễ dàng uống được chỉ với một vài thao tác rất đơn giản. Sau khi được gọt toàn bộ phần vỏ bên ngoài, quả dừa sẽ xuất hiện các mắt dừa và đây chính là nơi để bạn cắm ống hút vào uống.</span></p><p><span style=\"font-size:14px\">Sau khi đã uống xong phần nước dừa, tiếp theo bạn sẽ dùng sống dao đập nhẹ ở xung quanh đầu nhọn của quả dừa và nó sẽ tự động nứt ra. Lúc này, bạn cần dùng tay tách nhẹ nhàng lớp vỏ này ra để ăn được phần cơm dừa bên trong.&nbsp;</span></p><h3><strong><span style=\"font-size:14px\">Cách bảo quản dừa Xiêm&nbsp;trọc Bến Tre để giữ được lâu</span></strong></h3><p><span style=\"font-size:14px\">Dừa gọt trọc có thể giữ được khoảng 30 ngày nếu như được bảo quản ở nhiệt độ chuẩn là 2 - 5 độ C mà không sợ có vi khuẩn xâm nhập nhờ lớp vỏ dày và cứng bảo vệ.&nbsp;</span></p><p><span style=\"font-size:14px\">Nếu bảo quản ở những nơi thoáng mát, tránh ánh nắng trực tiếp thì bạn có thể giữ được dừa gọt trọc trong khoảng 1 tuần.</span></p><h3><strong><span style=\"font-size:14px\">Mua dừa Xiêm trọc Bến&nbsp;Tre&nbsp;ở đâu tại tp HCM</span></strong></h3><p><span style=\"font-size:14px\">Tình trạng thị trường trái cây phong phú hiện nay với sự xuất hiện của các mặt hàng không rõ nguồn gốc, kém chất lượng, thật giả lẫn lộn thì Bio Ngon&nbsp;là địa chỉ tin cậy để người tiêu dùng ghé mua trái cây, đặc biệt là mua dừa Xiêm trọc Bến Tre&nbsp;ở TPHCM.</span></p><p><span style=\"font-size:14px\">Các loại trái cây tại Bio Ngon đạt an toàn vệ sinh, đúng chất lượng, canh tác theo hướng hữu cơ tiêu chuẩn 6 KHÔNG, nhằm đảm bảo cho sức khỏe cho mọi nhà.</span></p><p><span style=\"font-size:14px\">Tiêu chuẩn 6 KHÔNG:</span></p><p><span style=\"font-size:14px\">•&nbsp;&nbsp;&nbsp;&nbsp;Không hóa chất kích thích tăng trưởng.</span></p><p><span style=\"font-size:14px\">•&nbsp;&nbsp;&nbsp;&nbsp;Không hóa chất diệt côn trùng, sâu hại.</span></p><p><span style=\"font-size:14px\">•&nbsp;&nbsp;&nbsp;&nbsp;Không hóa chất diệt cỏ.</span></p><p><span style=\"font-size:14px\">•&nbsp;&nbsp;&nbsp;&nbsp;Không hóa chất bảo quản.</span></p><p><span style=\"font-size:14px\">•&nbsp;&nbsp;&nbsp;&nbsp;Không hóa chất chống úng.</span></p><p><span style=\"font-size:14px\">•&nbsp;&nbsp;&nbsp;&nbsp;Không phân bón hóa học.</span></p><h3><strong><span style=\"font-size:14px\">Mua dừa Xiêm trọc&nbsp;trực tiếp tại website hoặc liên hệ:</span></strong></h3><p><span style=\"font-size:14px\">Fanpage: <strong><a href=\"http://m.me/biongon.raulanhcanhngot\">Bio Ngon</a></strong></span></p><p><span style=\"font-size:14px\">Hotline:&nbsp;0786416477</span></p><p><span style=\"font-size:14px\">Zalo:&nbsp;<a href=\"https://zalo.me/2384430608336986569\"><strong>Bio Ngon</strong></a></span></p></div>', '10', '20', '1','3');
